"CREATE OR REPLACE PROCEDURE public.pr_get_courseapproverlist(_user_code udd_code, _role_code udd_code, _lang_code udd_code, INOUT _result_courseapproval refcursor DEFAULT 'rs_courseapprovallst'::refcursor)
 LANGUAGE plpgsql
