 LANGUAGE plpgsql
AS $procedure$
DECLARE
	/*
		Created By   : Mangai
		Created Date : 07-12-2022
		
		Updated By   : Mangai
		Updated Date : 04-02-2023
		
		SP Code      : B03FEDG01
