			v_status_code := '';
	end if;
	
	-- drop temporary table
		drop table if exists trng_tmp_tvenueid;
		
	-- create temporary table for course_id
	vsql = 'create temporary table trng_tmp_tvenueid as 
			select 	v.venue_id
			from 	trng_mst_tvenue v
			left join trng_mst_tvenueaddr as va
