	 if _venue_type_code <> '' then
		 vsql = vsql || ' and v.venue_type_code = ''' || _venue_type_code || '''';
	 end if;

	if _venue_subtype_code <> '' then
		 vsql = vsql || ' and v.venue_subtype_code = ''' || _venue_subtype_code || '''';
	 end if;
	
	-- execute the query variable vsql
	execute vsql;
	
