									and coalesce(v_village_code,'') = case when coalesce(v_village_code,'') = '' then '' else coalesce(va.village_code,'') end 
									--and     status_code = 'A'
									and     v.others_flag = 'N'
									order by v.row_timestamp desc;
	
								
								
								
								
								/*select 		
