			insert into core_mst_tmaster 
						(
							parent_code,
							master_code,
							depend_parent_code,
							depend_code,
							rec_slno,
							sys_flag,
							status_code,
							created_by,
							created_date,
							row_timestamp
						)
						values
						(
							'SYS',
							_master_code,
							_depend_parent_code,
							_depend_code,
							0,
							'Y',
							'A',
							_user_code,
							now(),
							now()
						);
		end if;
		
		if not exists(select 	* 
					  from		core_mst_tmaster
					  where		master_gid = _master_gid 
					  and 		parent_code = _parent_code
					  and 		master_code = _master_code 
					  and 		status_code = 'A'
					 ) then
			insert into core_mst_tmaster 
			(
				parent_code,
				master_code,
				depend_parent_code,
				depend_code,
				rec_slno,
				sys_flag,
				status_code,
				created_by,
				created_date,
				row_timestamp
			)
			values
			(
				_parent_code,
				_master_code,
				_depend_parent_code,
				_depend_code,
				_rec_slno,
				_sys_flag,
				_status_code,
				_user_code,
				now(),
				now()
			) returning master_gid into _master_gid;
			
			v_succ_code := 'SB01MASCUD_001';
			
		else
			-- Record already exists
			v_err_code := v_err_code || 'EB01MASCUD_002';
			v_err_msg  := v_err_msg ||  fn_get_msg('EB01MASCUD_002', _lang_code) || v_new_line;	
			
			RAISE EXCEPTION '%',v_err_code || '-' || v_err_msg;
		end if;
	elsif (_mode_flag = 'U') then 
		if  exists(select 	*
				   from 	core_mst_tmaster
				   where	master_gid = _master_gid 
				   and 		parent_code = _parent_code
				   and 		master_code = _master_code 
				   and 		status_code = 'A'
				   ) then
			update	core_mst_tmaster 
			set 	depend_parent_code = _depend_parent_code,
					depend_code = _depend_code,
					status_code = _status_code,
					rec_slno = _rec_slno,
					updated_by = _user_code,
					updated_date = now(),
					row_timestamp = now()
			where 	master_gid = _master_gid 
			and 	parent_code = _parent_code
			and 	master_code = _master_code 
			and 	status_code = 'A';
			
			v_succ_code := 'SB01MASCUD_002';
		else
