					   and 		status_code = 'A'
					  ) then
			v_err_code := v_err_code || 'VB04CNWCXD_010' || ',';
			v_err_msg  := v_err_msg ||  fn_get_msg('VB04CNWCXD_010', _lang_code) || v_new_line;	
