			v_err_msg  := v_err_msg ||  fn_get_msg('EB04CNWCXD_002', _lang_code) || v_new_line;	
			
			RAISE EXCEPTION '%',v_err_code || '-' || v_err_msg;
		end if;
	elsif (_mode_flag = 'U') then 
		if  exists(select 	*
				   from 	trng_mst_tnote
