	end if;
	
	-- View_flag set as Yes
	if _add_flag = 'Y' or _modifiy_flag = 'Y' or _auth_flag = 'Y' then
		_view_flag := 'Y';
	end if;
	
	-- Deny_flag set as No
	if _add_flag = 'N' and _modifiy_flag = 'N' and _auth_flag = 'N' and _view_flag = 'N' then
