		-- reject reason code validation
		if _status_code = 'R' and _reject_reason_code isnull 
		then
				v_err_code := v_err_code || 'VB07PRGCUD_040' || ',';
				v_err_msg  := v_err_msg ||  fn_get_msg('VB07PRGCUD_040', _lang_code)|| v_new_line;	
