		if _reject_reason_code notnull then
				if not exists (select 	* 
							   from 	core_mst_tmaster 
							   where 	parent_code = 'QCD_REJ_REASON'
							   and 		master_code = _reject_reason_code
							   and 		status_code = 'A'
