				v_err_msg  := v_err_msg ||  fn_get_msg('VB07PRGCUD_043', _lang_code) || v_new_line;	
			end if;
		end if;
		
		-- Approver id validation
		if _approver_id = '' and _status_code = 'S' and _user_level_code <> '99' then
