						and   status_code <> 'I') then
					v_err_code := v_err_code || 'VB07PRGCUD_018' || ',';
					v_err_msg  := v_err_msg ||  fn_get_msg('VB07PRGCUD_018', _lang_code)  || v_new_line;	
			end if;
