	elsif (_mode_flag = 'U') then 
		if  exists(select 	*
				   from 	trng_mst_ttrainingorgdomain
				   where	trngorgdomain_gid = _trngorgdomain_gid
				   and 		status_code    	  = 'A'
				   ) then
			update	trng_mst_ttrainingorgdomain 
			set 	vertical_code		= _vertical_code,
