						   where mobile_no = v_mobile_no
						   and 	 smstemplate_code = v_smstemplate_code
						   and 	 sms_text = v_sms_content
						   ) then
							call pr_iud_smstran (	_tprogram_id,
													_tprogrambatch_id,
													'',
													'',
													'',
													'',
													v_mobile_no,
													'',
													'',
													v_smstemplate_code,
													v_template_id,
													v_sms_content,
													_user_code,
													'TrainingCoordinator');

